import { useContext, createContext, useEffect, useState } from "react";

import {
  AuthErrorCodes,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
  signInWithEmailAndPassword,
  GoogleAuthProvider,
  signInWithPopup,
  signInWithRedirect,
  signOut
} from "firebase/auth";
import { auth, db } from "../firebase.config";
import { addDoc, collection, doc, setDoc } from "firebase/firestore";

const userContext = createContext();


export const useAuth = () => {
  return useContext(userContext);
};

const UserAuthContext = ({ children }) => {
  const [error, setError] = useState("");
  const [currentuser, setuser] = useState();

  const googleSignIn = () => {
    const provider = new GoogleAuthProvider();
    signIn
  }

  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      console.log(user);
      if (user) {
        setuser(user);
        console.log("u are logging");
      } else {
        // alert("u are logout")
      }
    });
  }, [currentuser]);
  // const SignUp = async (email, password, FirstName, LastName, collectionName) => {
  //   setError("");
  //   const result = await createUserWithEmailAndPassword(auth, email, password)
      // .then(async (result) => {
      //   console.log(result);
      //   try {
      //     // const docRef = await addDoc(collection(db, "users"), {
      //     //   FullName,
      //     //   userId: `${result.user.uid}`
      //     // });
      //     const ref = doc(db, collectionName, result.user.uid); 
      //     //const ref = doc(db, "customerCollection", result.user.uid);
      //     const docRef = await setDoc(ref, {
      //       FirstName,
      //       LastName,
      //       email,
      //       password,
      //     });
      //     alert("Wellcome new User create successfully");
      //     console.log("Document written with ID: ", docRef.id);
      //   } catch (e) {
      //     console.error("Error adding document: ", e);
      //   }
      // })
      // .catch((err) => {
      //   if (err.code === "auth/email-already-in-use") {
      //     setInterval(() => {
      //       setError("");
      //     }, 5000);
      //     setError("email already in use try another email");
      //   } else if (err.code === AuthErrorCodes.WEAK_PASSWORD) {
      //     setInterval(() => {
      //       setError("");
      //     }, 5000);
      //     setError("Password Must be 6 charecter");
      //   } else {
      //     setError(err.message);
      //   }
      // });
  //};

  const SignUp = async (email, password, FirstName, LastName, collectionName) => {
    setError("");
    try {
      const result = await createUserWithEmailAndPassword(auth, email, password);
      console.log(result);
  
      const userDocRef = doc(db, collectionName, result.user.uid);
      await setDoc(userDocRef, {
        FirstName,
        LastName,
        email,
        password,
      });
  
      alert("Welcome! New user created successfully");
      console.log("Document written with ID: ", userDocRef.id);
    } catch (error) {
      if (error.code === "auth/email-already-in-use") {
        setError("Email already in use. Please try another email.");
      } else if (error.code === AuthErrorCodes.WEAK_PASSWORD) {
        setError("Password must be at least 6 characters long.");
      } else {
        setError(error.message);
      }
    }
  };
  

  const SignIn = async (email, password) => {
    setError(""); // Clear previous errors
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      console.log("Login successful:", user.uid);
      alert("Login successful");
      // You can redirect to the home page here if needed
    } catch (signInError) {
      setError(signInError.message);
    }
  };
  const value = {
    SignUp,
    SignIn,
    error,
    currentuser,
  };
 
  return <userContext.Provider value={value}>{children}</userContext.Provider>;
};

export default UserAuthContext;
